//Import CSS

import './ExpenseItem.css';
//Custom Component
//Convention is to keep the function name same as file name
//We can have only one root element in return; therefore we wrapped everything inside div and returned div
function ExpenseItem() {
    const expenseDate = new Date(2021,2,28); //Year, Month (starts from 0), Date
    const expenseTitle = 'Car Insurance';
    const expenseAmount = 294.67;
  return (
    <div className = "expense-item"> 
      <div>{expenseDate.toISOString()}</div>
      <div className = "expense-item__description">
        <h2>{expenseTitle}</h2>
        <div className = "expense-item__price">Rs. {expenseAmount}</div>
      </div>
    </div>
  );
}

//You can even perform calculations like 2+2 or use math functions inside curly brackets.
export default ExpenseItem; //export function to make it available to other files
